import React, { useRef, useState, useEffect } from 'react';
import './App.css';

import nightModeIcon from './pics/dark-mode.png';
import sunIcon from './pics/sun.png';
import sendIcon from './pics/send.png';
import avatarpic from './pics/user.png';

import { initializeApp } from 'firebase/app';
import { getFirestore, collection, orderBy, query, addDoc, serverTimestamp } from 'firebase/firestore';
import { getAuth, GoogleAuthProvider, signInWithPopup, signOut } from 'firebase/auth';

import { useAuthState } from 'react-firebase-hooks/auth';
import { useCollectionData } from 'react-firebase-hooks/firestore';

// Firebase config
const firebaseConfig = {
  apiKey: "AIzaSyDbPNrpMoxEgKgh34NstKV09p-y9fZYBEY",
  authDomain: "chatroom-mann.firebaseapp.com",
  projectId: "chatroom-mann",
  storageBucket: "chatroom-mann.appspot.com",
  messagingSenderId: "452863378138",
  appId: "1:452863378138:web:2fec6ca2320f961f221be3",
  measurementId: "G-NVDFENKVZ8"
};

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const firestore = getFirestore(app);

function App() {
  const [user] = useAuthState(auth);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }, [darkMode]);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  return (
    <div className="App d-flex flex-column vh-100">
      <header className="navbar navbar-expand-lg navbar-dark bg-primary fixed-top">
        <div className="container-fluid d-flex justify-content-between align-items-center">
          <h1 className="navbar-brand m-0">Chatroom</h1>
          <div className="d-flex align-items-center">
            <button className="btn btn-link p-0 me-2" onClick={toggleDarkMode}>
              <img src={darkMode ? sunIcon : nightModeIcon} alt="Toggle Dark Mode" className="light-dark" />
            </button>
            <SignOut />
          </div>
        </div>
      </header>

      <section className="flex-grow-1 overflow-hidden pt-5">
        {user ? <ChatRoom /> : <SignIn />}
      </section>
    </div>
  );
}

function SignIn() {
  const signInWithGoogle = () => {
    const provider = new GoogleAuthProvider();
    signInWithPopup(auth, provider);
  };

  return (
    <div className="d-flex justify-content-center align-items-center h-100">
      <button className="btn btn-primary btn-lg" onClick={signInWithGoogle}>
        Sign in with Google
      </button>
    </div>
  );
}

function SignOut() {
  return auth.currentUser && (
    <button className="btn btn-danger" onClick={() => signOut(auth)}>Sign Out</button>
  );
}

function ChatRoom() {
  const dummy = useRef();
  const messagesRef = collection(firestore, 'messages');
  const q = query(messagesRef, orderBy('createdAt'));
  const [messages] = useCollectionData(q, { idField: 'id' });

  const [formValue, setFormValue] = useState('');

  const sendMessage = async (e) => {
    e.preventDefault();

    const { uid, photoURL } = auth.currentUser;

    await addDoc(messagesRef, {
      text: formValue,
      createdAt: serverTimestamp(),
      uid,
      photoURL
    });

    setFormValue('');
    dummy.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <>
      <main className="overflow-auto px-3 py-2 flex-grow-1">
        {messages && messages.map(msg => <ChatMessage key={msg.id} message={msg} />)}
        <span ref={dummy}></span>
      </main>

      <form onSubmit={sendMessage} className="d-flex align-items-center p-2 bg-light border-top fixed-bottom">
        <input 
          value={formValue} 
          onChange={(e) => setFormValue(e.target.value)} 
          placeholder="Write Here!" 
          className="form-control me-2"
        />
        <button type="submit" className="btn btn-primary d-flex align-items-center" disabled={!formValue}>
          <img src={sendIcon} alt="Send" className="send-icon" />
        </button>
      </form>
    </>
  );
}

function ChatMessage(props) {
  const { text, uid, photoURL } = props.message;

  const messageClass = uid === auth.currentUser.uid ? 'sent' : 'received';

  return (
    <div className={`d-flex align-items-center my-2 ${messageClass === 'sent' ? 'justify-content-end' : 'justify-content-start'}`}>
      <img src={photoURL || avatarpic} alt="Avatar" className="rounded-circle me-2" style={{ width: '40px', height: '40px' }} />
      <p className={`message-bubble ${messageClass}`}>{text}</p>
    </div>
  );
}

export default App;
